<?phpnamespace App\DTO\CoursesV2;use App\DTO\BaseDTO;use Illuminate\Support\Facades\Date;class CourseFilterPropsDto extends BaseDTO{    /**     * @param string $search     * @param array $targets     * @param int $type     * @param bool $for_sale     * @param Date $created_date     * @param int $per_page     */    public function __construct(        public string       $search,        public array        $targets,        public int          $type,        public bool         $for_sale,        public Date         $created_date,        public int          $per_page    )    {    }    public function toArray(): array    {        return [            'search' => $this->search,            'targets' => $this->targets,            'type' => $this->type,            'for_sale' => $this->for_sale,            'created_date' => $this->created_date,            'per_page' => $this->per_page,        ];    }}