<?phpnamespace App\DTO\CoursesV2;use App\DTO\BaseDTO;class CourseFilterPropsDto extends BaseDTO{    /**     * @param string|null $search     * @param int|null $profile_group_id     * @param int|null $position_id     * @param int|null $type     * @param bool $for_sale     * @param string|null $created_date     * @param int|null $per_page     */    public function __construct(        public string|null $search,        public int|null    $profile_group_id,        public int|null    $position_id,        public int|null    $type,        public bool|null   $for_sale,        public string|null $created_date,        public int|null    $per_page    )    {    }    public function toArray(): array    {        return [            'search' => $this->search,            'profile_group_id' => $this->profile_group_id,            'position_id' => $this->position_id,            'type' => $this->type,            'for_sale' => $this->for_sale,            'created_date' => $this->created_date,            'per_page' => $this->per_page,        ];    }}