<?phpnamespace App\Http\Resources\Articles\Poll;use App\Http\Resources\Users\UserResource;use App\Models\Article\PollAnswer;use Illuminate\Http\Resources\Json\JsonResource;/** * @property PollAnswer $resource */class ArticlePollAnswerResource extends JsonResource{    public function __construct($resource)    {        parent::__construct($resource);    }    public function toArray($request): array    {        return [            'id' => $this->resource->id,            'article_id' => $this->resource->article_id,            'question_id' => $this->resource->question_id,            'order' => $this->resource->order,            'answer' => $this->resource->answer,            'votes' => UserResource::collection($this->resource->votes)->toArray($request),        ];    }}