<?phpnamespace App\Http\Resources\Articles\Poll;use App\Models\Article\PollQuestion;use Illuminate\Http\Resources\Json\JsonResource;/** * @property PollQuestion $resource */class ArticlePollQuestionResource extends JsonResource{    public function __construct($resource)    {        parent::__construct($resource);    }    public function toArray($request): array    {        return [            'id' => $this->resource->id,            'article_id' => $this->resource->article_id,            'multi_answer' => $this->resource->multi_answer,            'question' => $this->resource->question,            'order' => $this->resource->order,            'answers' => ArticlePollAnswerResource::collection($this->resource->answers)        ];    }}