<?phpnamespace App\Service\CourseV2;use App\DTO\CoursesV2\AssignedCourseFilterPropsDto;use App\DTO\CoursesV2\CoursePropsDto;use App\Models\CourseItemV2;use App\Models\CourseTargetV2;use App\Models\CourseV2;use App\Repositories\CoursesV2\CentralCourseCatRepository;use Illuminate\Contracts\Pagination\LengthAwarePaginator;class CentralCourseCatService{    public function __construct(        public CentralCourseCatRepository $repository    )    {    }    public function getAll(): LengthAwarePaginator    {        return $this->repository->filter();    }    public function create(array $data)    {        return $this->repository->createCat($data);    }    public function update($category, $data)    {        return $this->repository->updateCat($category, $data);    }    public function delete($category)    {        return $this->repository->delete($category);    }}