<?phpuse App\Http\Controllers\CentralCourseCatController;use App\Http\Controllers\CentralCourseController;use App\Http\Controllers\CourseV2\CourseV2Controller;use App\Http\Controllers\CourseV2\LibraryController;use App\Http\Controllers\CourseV2\MyCourseV2Controller;// Tenant coursesRoute::get('index', [CourseV2Controller::class, 'getAll']);Route::post('create', [CourseV2Controller::class, 'create']);Route::get('get/{course}', [CourseV2Controller::class, 'getOne']);Route::post('update/{course}', [CourseV2Controller::class, 'update']);Route::delete('delete/{course}', [CourseV2Controller::class, 'destroy']);// Assigned coursesRoute::get('assigned', [CourseV2Controller::class, 'getAssigned']);// LibraryRoute::get('library/categories', [LibraryController::class, 'getCategories']);Route::get('library/courses', [LibraryController::class, 'getLatestCourses']);Route::get('library/courses/{course}', [LibraryController::class, 'getCourse']);Route::prefix('my-courses')->group(function () {    Route::get('index', [MyCourseV2Controller::class, 'getAll']);    Route::get('get/{course}', [MyCourseV2Controller::class, 'getOne']);    Route::post('assess',[MyCourseV2Controller::class, 'assessCourseAndCurator']);    Route::post('pass',[MyCourseV2Controller::class, 'pass']);});Route::prefix('admin')->group(function () {    Route::get('central-courses/index', [CentralCourseController::class, 'getAll']);    Route::get('central-categories/index', [CentralCourseCatController::class, 'getAll']);    Route::post('central-categories/create', [CentralCourseCatController::class, 'create']);    Route::get('central-categories/{category}', [CentralCourseCatController::class, 'getOne']);    Route::post('central-categories/{category}', [CentralCourseCatController::class, 'update']);    Route::delete('central-categories/{category}', [CentralCourseCatController::class, 'destroy']);});