<?phpuse App\Http\Controllers\CentralCourseCatController;use App\Http\Controllers\CentralCourseController;use App\Http\Controllers\CourseV2\CourseV2Controller;use App\Http\Controllers\CourseV2\LibraryController;Route::group([], function () {    // My courses    Route::get('index', [CourseV2Controller::class, 'getAll']);    Route::post('create', [CourseV2Controller::class, 'create']);    Route::get('get/{course}', [CourseV2Controller::class, 'getOne']);    Route::post('update/{course}', [CourseV2Controller::class, 'update']);    Route::delete('delete/{course}', [CourseV2Controller::class, 'destroy']);    // Assigned courses    Route::get('assigned', [CourseV2Controller::class, 'getAssigned']);    // Library    Route::get('library/categories', [LibraryController::class, 'getCategories']);    Route::get('library/courses', [LibraryController::class, 'getLatestCourses']);    Route::get('library/courses/{course}', [LibraryController::class, 'getCourse']);});Route::group([], function () {    Route::get('admin/central-courses/index', [CentralCourseController::class, 'getAll']);    Route::get('admin/central-categories/index', [CentralCourseCatController::class, 'getAll']);    Route::post('admin/central-categories/create', [CentralCourseCatController::class, 'create']);    Route::get('admin/central-categories/{category}', [CentralCourseCatController::class, 'getOne']);    Route::post('admin/central-categories/{category}', [CentralCourseCatController::class, 'update']);    Route::delete('admin/central-categories/{category}', [CentralCourseCatController::class, 'destroy']);});